import { useState, useEffect } from "react";
import DotGroup from "./sceens/DotGroup";
import Landing from './sceens/Landing';
import useMediaQuery from "./hooks/useMediaQuery";
import Navbar from "./sceens/Navbar";
import LineGradient from './components/LineGradient';
import MySkills from './sceens/MySkills';
import Projects from './sceens/Projects';
import Testimonials from './sceens/Testimonials';
import Contact from './sceens/Contact';
import Footer from './sceens/Footer';
imp

function App() {
  const [selectedPage, setSelectedPage] = useState("home");
  const [isTopOfPage, setIsTopOfPage] = useState(true);
  const isDesktop = useMediaQuery("(min-width: 1060px)");

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY !== 0)
        setIsTopOfPage(false);
      if (window.scrollY === 0)
        setIsTopOfPage(true);
      if (window.scrollY > 0 && window.scrollY <= 771) {
        setSelectedPage("home");
      }
      else if (window.scrollY <= 1542) {
        setSelectedPage("skills");
      }
      else if (window.scrollY <= 2976) {
        setSelectedPage("projects");
      }
      else if (window.scrollY <= 3320) {
        setSelectedPage("testimonials");
      }
      else {
        setSelectedPage("contact");
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="app bg-deep-blue">
      <Navbar
        isTopOfPage={isTopOfPage}
        selectedPage={selectedPage}
        setSelectedPage={setSelectedPage}
      />
      <div className="w-5/6 mx-auto md:h-full">
        {isDesktop && (
          <DotGroup
            selectedPage={selectedPage}
            setSelectedPage={setSelectedPage}
          />
        )}
        <motion.div
          margin="0 0 -200px 0"
          amount="all"
          onViewportEnter={() => setSelectedPage("home")}
        >
          <Landing setSelectedPage={setSelectedPage} />
        </motion.div>
      </div>
      <LineGradient />
      <div className="w-5/6 mx-auto md:h-full ">
        <motion.div
          margin="0 0 -200px 0"
          amount="all"
          onViewportEnter={() => setSelectedPage("skills")}
        >
          <MySkills />
        </motion.div>
      </div>
      <LineGradient />
      <div className="w-5/6 mx-auto">
        <motion.div
          margin="0 0 -200px 0"
          amount="all"
          onViewportEnter={() => setSelectedPage("projects")}
        >
          <Projects />
        </motion.div>
      </div>
      <LineGradient />
      <div className="w-5/6 mx-auto md:h-full">
        <motion.div
          margin="0 0 -200px 0"
          amount="all"
          onViewportEnter={() => setSelectedPage("testimonials")}
        >
          <Testimonials />
        </motion.div>
      </div>
      <LineGradient />
      <div className="w-5/6 mx-auto md:h-full">
        <motion.div
          margin="0 0 -200px 0"
          amount="all"
          onViewportEnter={() => setSelectedPage("contact")}
        >
          <Contact />
        </motion.div>
      </div>
      <Footer />
    </div>
  );
}

export default App;
